/* eslint-disable react/no-multi-comp */
/* eslint-disable react/display-name */
import React from 'react';
import clsx from 'clsx';
import PropTypes from 'prop-types';
import { makeStyles } from '@material-ui/core/styles';
import {
  List,
  ListItem,
  Typography,
  ListItemIcon,
  Divider,
  Button,
  Box,
} from '@material-ui/core';
import CloseIcon from '@material-ui/icons/Close';
import { Image, } from 'components/atoms';


const useStyles = makeStyles(theme => ({
  root: {
  },
  listItem: {
    flexDirection: 'column',
    alignItems: 'center',
  },
  navLink: {
    '&:hover': {
      color: theme.palette.primary.dark,
    },
  },
  logoContainer: {
    alignItems: 'center',
    [theme.breakpoints.up('md')]: {
      width: 120,
      height: 32,
    },
  },
  logoImage: {
    width: '60%',
    height: '100%',
  },
  listItemIcon: {
    minWidth: 'auto',
  },
  closeIcon: {
    justifyContent: 'flex-end',
    cursor: 'pointer',
  },
  menu: {
    display: 'flex',
  },
  menuItem: {
    marginRight: theme.spacing(8),
    '&:last-child': {
      marginRight: 0,
    },
  },
  menuGroupItem: {
    paddingTop: 0,
  },
  menuGroupTitle: {
    textTransform: 'uppercase',
  },
  divider: {
    width: '100%',
  },
}));

const SidebarNav = props => {
  const { pages, themeMode,onClose, className, ...rest } = props;
  const classes = useStyles();

  const MenuGroup = props => {
    var pagesdict={} ;
      for (let i=0;i<pages.length;i++){
          if (pages[i]==='Home'){
            pagesdict[pages[i]]='/'
          }
          else{
            pagesdict[pages[i]]=pages[i].toLowerCase().replace(" ","");
          }
      }
    return (
      <List  dense>

        {pages.map((page, i) => (
          <>
          <ListItem disableGutters key={i} className={classes.listItem}>
            <Typography
              variant="h5"
              component={'a'}
              href={pagesdict[page]}
              color="textPrimary"
              gutterBottom
              onClick={() => onClose()}
            >
              {page}
            </Typography>
          </ListItem>
           <ListItem className={classes.listItem}>
              <Divider className={classes.divider} />
           </ListItem>
          </>
        ))}
      </List>
    );
  };




  return (
    <List {...rest} className={clsx(classes.root, className)}>

      <ListItem className={classes.closeIcon} onClick={() => onClose()}>
        <ListItemIcon className={classes.listItemIcon}>
          <CloseIcon fontSize="small" />
        </ListItemIcon>
      </ListItem>
      <ListItem className={classes.listItem}>
      <Box justifyContent={'center'} className={classes.logoContainer} style={{border:'2px solid yellow'}}>
        <a href="/" title="thefront" style={{border:'2px solid yellow'}}>
          <Image
            className={classes.logoImage}
            src={themeMode === 'light' ? 'https://assets.maccarianagency.com/the-front/logos/logo.svg' : 'https://assets.maccarianagency.com/the-front/logos/logo-negative.svg'}
            alt="thefront"
            lazy={false}
          />
        </a>
      </Box>
      </ListItem>
      <MenuGroup pages={pages}/>


      <ListItem className={classes.listItem}>
        <Button
          variant="outlined"
          fullWidth
          component="a"
          href="/documentation"
        >
          Documentation
        </Button>
      </ListItem>
      <ListItem className={classes.listItem}>
        <Button
          variant="contained"
          color="primary"
          fullWidth
          component="a"
          target="blank"
          href="https://material-ui.com/store/items/the-front-landing-page/"
        >
          Buy Now
        </Button>
      </ListItem>
    </List>
  );
};

SidebarNav.propTypes = {
  className: PropTypes.string,
  pages: PropTypes.array.isRequired,
  onClose: PropTypes.func,
  themeMode: PropTypes.string.isRequired,

};

export default SidebarNav;
